import React, { useEffect, useState } from "react";
import "./App.css";
import Register from "./Components/Register";
import List from "./Components/List";
import Task from "./Components/Task";
import GlobalStyle from "./Style/GlobalStyle"; 


function App() {

    const [isRegistered, setIsRegistered] = useState(false);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [tasks, setTasks] = useState([]);

    const registrationHandler = (event) => {
        setEmail(email);
        setPassword(password);
        setIsRegistered(true);
        event.preventDefault();
    };

    useEffect(() => {
        localStorage.setItem('tasks', JSON.stringify(tasks));
    }, [tasks]);

    useEffect(() => {
        const tasks = JSON.parse(localStorage.getItem('tasks'));
        setTasks(tasks);
    }, []);

    const addTask = (name) => {
        setTasks(prevTasks => [...prevTasks, { name: name, done: false }]);
    };

    function removeTask(indexToRemove) {
        setTasks(prev => {
            return prev.filter((taskObject, index) => index !== indexToRemove);
        });

    }

    function updateTaskDone(taskIndex, newdone) {
        setTasks(prev => {
            const newTasks = [...prev];
            newTasks[taskIndex].done = newdone;
            return newTasks;
        });
    }

    return (
        <><GlobalStyle/> <div className="main">
            <div className="container register-container card pt-3 pb-3">

                {isRegistered ? (
                    <>
                        <List onAdd={addTask}></List>
                        {tasks.map((task, index) => (
                            <Task key={task.name} {...task}
                                onTrash={() => removeTask(index)}
                                onToggle={done => updateTaskDone(index, done)} />
                        ))}
                    </>
                ) : (
                    <Register submit={registrationHandler}></Register>
                    // <Register></Register>
                )}
            </div>
        </div></>
    );
};

export default App;
