import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
 body {
    background-color: #17181f;
    color: #ddd;
 }
  h1 {
    font-size: 72px;
    colo: #61dafb;
  }
  .main {
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
  }
`;

export default GlobalStyle;
