import React from "react";
import Checkbox from "./Checkbox";

export default function Task({ name, done, onToggle, onTrash }) {
  const CheckboxInput = styled.input`
  &:checked, &:not(:checked) {
  display: none;
 }`;

const CheckboxLabel = styled.label`
  cursor: pointer;
  -webkit-user-select: none; /* Chrome/Safari */
  -moz-user-select: none; /* Firefox */
  -ms-user-select: none; /* IE10+ */
`;

function Checkbox(props) {
  return (
    <CheckboxLabel htmlFor="id" onChange={() => { props.onchange(); }}>
      <CheckboxInput id="id" type="checkbox" checked={props.checked} value="cb" name="cb" />

      {props.children}
    </CheckboxLabel>
  );
}
  return (
    <div className={'task ' + (done ? 'done' : '')}>

      <Checkbox checked={done} onClick={() => onToggle(!done)} />
      <div className="task-name"><span></span>{name}</div>
      <button className="trash" onClick={onTrash} >
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512"><path d="M135.2 17.7L128 32H32C14.3 32 0 46.3 0 64S14.3 96 32 96H416c17.7 0 32-14.3 32-32s-14.3-32-32-32H320l-7.2-14.3C307.4 6.8 296.3 0 284.2 0H163.8c-12.1 0-23.2 6.8-28.6 17.7zM416 128H32L53.2 467c1.6 25.3 22.6 45 47.9 45H346.9c25.3 0 46.3-19.7 47.9-45L416 128z" /></svg></button>

    </div>
  );
}
